class ToggleWow{constructor(t){this.toggler=t,this.toggleWow()}toggleWow(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"GET",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-wow"));if(console.log(a),1==t.data.deleted){if(0==t.data.model_diff&&(a-=1),1==t.data.model_diff){a+=1;let e,o="post-"+t.data.model+"-"+t.data.post_id;console.log("query is",o),"like"==t.data.model?(e=parseInt($("#"+o).attr("data-likes")),0!=e&&(e-=1),$("#"+o).html(e+" Likes")):(e=parseInt($("#"+o).attr("data-"+t.data.model)),0!=e&&(e-=1),$("#"+o).html(`<i class="fas fa-heart" style="color: red;">&nbsp${e} Love</i>`))}}else a+=1;$(e).attr("data-wow",a),$(e).html(`<i class="fas fa-surprise" style="color: yellowgreen;">&nbsp${a} Wow</i>`)})).fail((function(t){console.log("error in completing the request")}))}))}}